import React from 'react';
import Header from './header';
import Slider from './slider';
import "../assets/css/style.css"
import "../App.css";
import { connect } from 'react-redux';
import {fetchFashion} from '../actions/index';



class Fashion extends React.Component {
    componentDidMount() {
        this.props.fetchFashion();
    }
    render() {
        const { fashion } = this.props;
        return (
            <div>
                <Header />
                <Slider heading={' Fashion page'} />
                <section className="category-area section-padding30" >
                    <div className="container-fluid">
                        {/* Section Tittle */}
                    </div>
                </section>
                <section className="latest-product-area padding-bottom">
                    <div className="container">
                        <div className="row product-btn d-flex justify-content-end align-items-end">
                            {/* Section Tittle */}
                            <div className="col-xl-4 col-lg-5 col-md-5">
                                <div className="section-tittle mb-30">
                                    <h3>Shop By category</h3>
                                </div>
                            </div>
                            <div className="col-xl-8 col-lg-7 col-md-7">
                                <div className="properties__button f-right">
                                    {/*Nav Button  */}
                                    
                                    {/*End Nav Button  */}
                                </div>
                            </div>
                        </div>
                        {/* Nav Card */}
                        <div className="tab-content" id="nav-tabContent">
                            {/* card one */}
                            <div className="tab-pane fade show active" id="nav-home" role="tabpanel" aria-labelledby="nav-home-tab">
                                <div className="row">
                                    {fashion && fashion.map((fashion,i) => (
                                        <div className="col-xl-4 col-lg-4 col-md-6" key={i}>
                                        <div className="single-product mb-60">
                                                <div className="product-img">
                                                    <a href={`single_product/${fashion._id}`}>
                              <img src={`http://localhost:3000/${fashion.photo[0].path}`} alt="logo" />
                              </a>
                                                <div className="new-product">
                                                    <span>New</span>
                                                </div>
                                            </div>
                                            <div className="product-caption">
                                                <div className="product-ratting">
                                                    <i className="far fa-star" />
                                                    <i className="far fa-star" />
                                                    <i className="far fa-star" />
                                                    <i className="far fa-star low-star" />
                                                    <i className="far fa-star low-star" />
                                                    </div>
                                                       <h4><a href={`single_product/${fashion._id}`}>{fashion.title}</a></h4>
                                                <div className="price">
                                                    <ul>
                                                <li>${fashion.price}</li>
                                                <li className="discount">${fashion.discount}</li>
                                                    </ul>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                    ))}
                                    
                                </div>
                            </div>
                            {/* Card two */}
     
                            {/* card foure */}
     
                        </div>
                        {/* End Nav Card */}
                    </div>
                </section>

            </div>
        )
    }
}
const mapStateToProps = state => {  
  return {
    fashion:state.products
  }
}

export default connect(mapStateToProps,{fetchFashion})(Fashion);
