import { combineReducers } from "redux";

function productsApp(state = [], action) {
        
    switch (action.type) {
        case "FETCH_PRODUCTS":
            return [...state, action.payload]
        case "FETCH_PRODUCT":
            const products = [...state, action.payload]
            
            return products;
        
        default:
      return state;
    }
}


export default combineReducers({
    products:productsApp
})
