import React from 'react';
import Header from './Components/header';
import Slider from './Components/slider';
import Footer from './Components/footer';
import axios from 'axios';
import { Link } from 'react-router-dom';

class Login extends React.Component {
  state = {
        email: '',
        password: '',
       open: false,
    error: '',
        

    }
  handleChange = (name) => e => {
        this.setState({ error: "" });
    this.setState({[name]:e.target.value})
  }
  
  clickSubmit = event => {
    event.preventDefault();
    this.setState({ loading: true });
    const { email, password } = this.state;
    const user = {
      email,
      password
    };
    let formData = new FormData(); //formdata object

    formData.append("password", password); //append the values with key, value pair
    formData.append("email", email);

    this.SignIn(user);
  };
  SignIn = async user => {
    return await axios({
      method: "post",
      url: "http://localhost:3000/signIn",
      data: user
    })
      .then(res => {
        if (typeof window !== "undefined") {
          localStorage.setItem("jwt", JSON.stringify(res.data));
          this.setState({ redirectToReferer: true });
        }
        this.setState({
          error: "",
          email: "",
          password: "",
          open: true
        });
        return JSON.stringify(res.data);
      })
      .catch(error => {
        // Error
        if (error.response) {
          // The request was made and the server responded with a status code
          // that falls out of the range of 2xx
          this.setState({ error: error.response.data.message, loading: false });
          console.log(
            "error.response.data.message",
            error.response.data.message
          );
          // console.log(error.response.status);
          // console.log(error.response.headers);
        } else if (error.request) {
          // The request was made but no response was received
          // `error.request` is an instance of XMLHttpRequest in the
          // browser and an instance of
          // http.ClientRequest in node.js
          console.log(error.request);
        } else {
          // Something happened in setting up the request that triggered an Error
          console.log("Error", error.message);
        }
      });
  };
  render() { 
    const {  email, password,error } = this.state;
      
        return (  <div>
  <Header/>
  {/* slider Area Start*/}
          <Slider heading={'Login'}/>
  {/* slider Area End*/}
  {/*================login_part Area =================*/}
  <section className="login_part section_padding ">
    <div className="container">
      <div className="row align-items-center">
        <div className="col-lg-6 col-md-6">
          <div className="login_part_text text-center">
            <div className="login_part_text_iner">
              <h2>New to our Shop?</h2>
              <p>There are advances being made in science and technology
                everyday, and a good example of this is the</p>
                      <Link to="/signup" className="btn_3">Create an Account</Link>
            </div>
          </div>
        </div>
        <div className="col-lg-6 col-md-6">
          <div className="login_part_form">
            <div className="login_part_form_iner">
              <h3>Welcome Back ! <br />
                Please Sign in now</h3>
                      <div
            className="alert alert-danger"
            style={{ display: error ? "" : "none" }}
          >
            {error}
          </div>
            <form onSubmit={this.clickSubmit} className="row contact_form" action="#" method="post"  noValidate="novalidate">
                
                <div className="col-md-12 form-group p_star">
                  <input required type="email" value={email} onChange={this.handleChange("email")} className="form-control" id="email" name="email"  placeholder="email" />
                </div>
                
                <div className="col-md-12 form-group p_star">
                  <input type="password"value={password} onChange={this.handleChange("password")} className="form-control" id="password" name="password"  placeholder="Password" />
                                        </div>
                
                <div className="col-md-12 form-group">
                    
                  {/* <div className="creat_account d-flex align-items-center">
                    <input type="checkbox" id="f-option" name="selector" />
                    <label htmlFor="f-option">Remember me</label>
                  </div> */}
                  <button type="submit" value="submit" className="btn_3">
                    Login
                  </button>
                  {/* <a className="lost_pass" href="#">forget password?</a> */}
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  </section>
          {/*================login_part end =================*/}
          <Footer />
          
</div>
);
    }
}
 
export default Login;
