import React,{useEffect} from "react";
import {
  MDBCarousel,
  MDBMask,
  MDBCarouselCaption,
  MDBCarouselInner,
  MDBCarouselItem,
  MDBView,
  MDBContainer
} from "mdbreact";
import { fetchProduct } from '../redux/actions/index';
import { connect } from 'react-redux';
const CarouselPage = (props) => {
  useEffect(() => {
    props.fetchProduct(props.id);
      
    },[])

  // if (props.products.length>0) {
    return (
      <div>
        <MDBContainer style={{ height: "30em" }}>
          <MDBCarousel
            activeItem={1}
            color={"blue"}
            length={2}
            showControls={true}
            showIndicators={true}
            className="z-depth-1"
          // slide
          >
            <MDBCarouselInner>
              {props.products &&
                props.products.photo.map((photo, i) => (
                  <MDBCarouselItem itemId={i + 1}>
                    <MDBView>
                      <img
                        className="d-block w-100"
                        src={`http://localhost:3000/${photo.path}`}
                        alt="First slide"
                      />

                      <MDBMask overlay="black-light" />
                    </MDBView>
                    <MDBCarouselCaption>
                      <h3 className="h3-responsive text-light">
                        {props.products.title}
                      </h3>
                    </MDBCarouselCaption>
                  </MDBCarouselItem>
                ))}
            </MDBCarouselInner>
          </MDBCarousel>
        </MDBContainer>
        
      </div>
    );
  // }
  // return <div>loading.....</div>
};


const mapDispatchToProps = (dispatch) => ({
  fetchProduct: (id) => {
    dispatch(fetchProduct(id))
  },
})


const mapStateToProps = (state) => {
  console.log(state.products.products,"products");
  return {
    products: state.products.products
  }
  
}

export default connect(mapStateToProps,mapDispatchToProps)(CarouselPage);
