import React from 'react';
import Header from './Components/header';
import Slider from './Components/slider';
import Footer from './Components/footer';
import CarouselPage from './Components/carosoul';
import { connect } from 'react-redux';
import { fetchProduct } from './actions/index'
import './assets/css/style.css'
class SingleProduct extends React.Component {
  state = {}
  componentWillMount() {
    this.props.fetchProduct(this.props.match.params.id);
  }
  render() { 
    const { products } = this.props;

      return (<div>
          <Header/>
  {/* slider Area Start*/}
        <Slider heading={"Single Product"}/>
  {/* slider Area End*/}
  {/*================Single Product Area =================*/}
  <div className="product_image_area">
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-lg-12">
                <CarouselPage products={this.props.products}/>
              </div>
              {products && products.map(products => (
        <div className="col-lg-8">
          <div className="single_product_text text-center">
                  <h3>{ products.title }</h3>
                  <p>
                    { products.body }
            </p>
            <div className="card_area">
              <div className="product_count_area">
                <p>Quantity</p>
                <div className="product_count d-inline-block">
                  <span className="product_count_item inumber-decrement"> <i className="ti-minus" />-</span>
                  <input className="product_count_item input-number" type="text" defaultValue={1} min={0} max={10} />
                  <span className="product_count_item number-increment"> <i className="plus" /></span>
                </div>
                <p>$5</p>
              </div>
              <div className="add_to_cart">
                <a href="/cart" className="btn_3">add to cart</a>
              </div>
            </div>
          </div>
        </div>        
              ))}

      </div>
    </div>
  </div>
  {/*================End Single Product Area =================*/}
  {/* subscribe part here */}
  <section className="subscribe_part section_padding">
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-lg-8">
          <div className="subscribe_part_content">
            <h2>Get promotions &amp; updates!</h2>
            <p>Seamlessly empower fully researched growth strategies and interoperable internal or “organic” sources credibly innovate granular internal .</p>
            <div className="subscribe_form">
              <input type="email" placeholder="Enter your mail" />
              <a href="#" className="btn_1">Subscribe</a>
            </div>
          </div>
        </div>
      </div>
    </div>
  </section>
        {/* subscribe part end */}
        <Footer />

</div>
 );
    }
}
const mapStateToProps = (state) => {
  // console.log(state.products);

  return {
    products: state.products
  }

}

export default connect(mapStateToProps,{fetchProduct})(SingleProduct)
