import React, { useState, useEffect}from 'react';
import {Link, Redirect, withRouter} from 'react-router-dom'
import { isAuthenticated } from '../auth';
import { connect } from 'react-redux';
import { fetchRecord } from '../actions/index';
import Search from './search';
import Slider from './slider';
import FashionCoursal from './main_carsoul';
import '../App.css'
class Header extends React.Component {
  state = {
    redirect: false,
    search:''
  }
  
   handleChange = e => {
    // setSearch(e.target.value)
     this.setState({search:e.target.value})
  }
  handleSearch = (event) => {
    const { search,redirect } = this.state;
    event.preventDefault();
     this.setState({redirect:!redirect})
    console.log(search);
    this.props.dispatch(fetchRecord(search));
    // console.log(fashion);

  }
  render() {
    const { redirect,search } = this.state;
    if (this.props.fashion && this.props.fashion.length > 0) {
      if (redirect) {
        return (
          <div>
            <div>
              <Header />
              <section className="category-area section-padding20 mt-4" >
                <div className="container-fluid">
                  {/* Section Tittle */}
                </div>
              </section>
              <section className="latest-product-area padding-bottom mt-4">
                <div className="container">
                  <div className="row product-btn d-flex justify-content-end align-items-end">
                    {/* Section Tittle */}
                    <div className="col-xl-4 col-lg-5 col-md-5">
                      <div className="section-tittle mb-30">
                        <h3>Shop By category</h3>
                      </div>
                    </div>
                    <div className="col-xl-8 col-lg-7 col-md-7">
                      <div className="properties__button f-right">
                        {/*Nav Button  */}
                                    
                        {/*End Nav Button  */}
                      </div>
                    </div>
                  </div>
                  {/* Nav Card */}
                  <div className="tab-content" id="nav-tabContent">
                    {/* card one */}
                    <div className="tab-pane fade show active" id="nav-home" role="tabpanel" aria-labelledby="nav-home-tab">
                      <div className="row">
                        {this.props.fashion && this.props.fashion.map((fashion, i) => (
                          <div className="col-xl-4 col-lg-4 col-md-6" key={i}>
                            <div className="single-product mb-60">
                              <div className="product-img">
                                <a href={`single_product/${fashion._id}`}>
                                  <img src={`http://localhost:3000/${fashion.photo[0].path}`} alt="logo" />
                                </a>
                                <div className="new-product">
                                  <span>New</span>
                                </div>
                              </div>
                              <div className="product-caption">
                                <div className="product-ratting">
                                  <i className="far fa-star" />
                                  <i className="far fa-star" />
                                  <i className="far fa-star" />
                                  <i className="far fa-star low-star" />
                                  <i className="far fa-star low-star" />
                                </div>
                                <h4><a href={`single_product/${fashion._id}`}>{fashion.title}</a></h4>
                                <div className="price">
                                  <ul>
                                    <li>${fashion.price}</li>
                                    <li className="discount">${fashion.discount}</li>
                                  </ul>
                                </div>
                              </div>
                            </div>
                          </div>
                        ))}
                                    
                      </div>
                    </div>
                    {/* Card two */}
     
                    {/* card foure */}
     
                  </div>
                  {/* End Nav Card */}
                </div>
              </section>

            </div>
          </div>
        )
      }
    }
   


    return (
      <header>
<nav className="navbar navbar-expand-lg navbar-dark bg-dark">
  <div className="container-fluid">
    <a className="navbar-brand" href="#">
      <img className="logo horizontal-logo" src="horizontal-logo.svg" alt="forecastr logo" />
    </a>
    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon" />
    </button>
    <div className="collapse navbar-collapse" id="navbarSupportedContent">
      <ul className="navbar-nav ml-auto">
        <li className="nav-item">
          <a className="nav-link" href="#">Link</a>
        </li>
        <li className="nav-item dropdown">
          <a className="nav-link dropdown-toggle" href="#" id="navbarDropdown1" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            Dropdown
          </a>
          <div className="dropdown-menu" aria-labelledby="navbarDropdown1">
            <a className="dropdown-item" href="#">Action</a>
            <a className="dropdown-item" href="#">Another action</a>
            <div className="dropdown-divider" />
            <a className="dropdown-item" href="#">Something else here</a>
          </div>
        </li>
        <li className="nav-item dropdown">
          <a className="nav-link dropdown-toggle" href="#" id="navbarDropdown2" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            Dropdown
          </a>
          <div className="dropdown-menu" aria-labelledby="navbarDropdown2">
            <a className="dropdown-item" href="#">Action</a>
            <a className="dropdown-item" href="#">Another action</a>
            <div className="dropdown-divider" />
            <a className="dropdown-item" href="#">Something else here</a>
          </div>
        </li>
        ...
      </ul>
    </div>
  </div>
</nav>

      </header>
    )
  }
}
const mapStateToProps = (state) => {
  // console.log(state.products[1]);
  return {
    fashion:state.products[1]
  }
  
}



export default connect(mapStateToProps)(Header);
