import { combineReducers } from "redux";
const initialState = {
}
function productsApp(state = initialState, action) {
        
    switch (action.type) {
        case "FETCH_PRODUCTS":
            return {...state, products:action.payload}
        case "FETCH_PRODUCT":
            return {...state, products:action.payload}        
        case "FETCH_WOMEN":
            return {...state, products:action.payload}        
        case "FETCH_MEN":
            return {...state, products:action.payload}        
        case "FETCH_SPORTS":
            return {...state, products:action.payload}        
        case "FETCH_ELECTRONICS":
            return {...state, products:action.payload}
        case "FETCH_FASHION":
            return {...state, products:action.payload}

        case "FETCH_SEARCH":
            return {...state, products:action.payload}
        default:
      return state;
    }
}


function postsApp(state = initialState, action) {
        
    switch (action.type) {
        case "FETCH_BLOGS":
            return {...state, posts:action.payload}
        case "FETCH_BLOG":
            return {...state, posts:action.payload}
        
        default:
      return state;
    }
}


function Auth(state = [], action) {
        
    switch (action.type) {
        case "LOGIN":
            return [...state, action.payload]
        case "SIGNUP":
            return [...state, action.payload]
        
        default:
      return state;
    }
}

function Cart(state = [], action) {
        
    switch (action.type) {
        case "CREATE_CART":
            return [...state, action.payload]
        case "FETCH_CARTS":
            return [...state, action.payload]
        
        default:
      return state;
    }
}



export default combineReducers({
    products: productsApp,
    posts: postsApp,
    Auth: Auth,
    Cart:Cart
})
