import React from 'react';

export default function Slider({header}) {
    return (
        <div className="slider-area ">
    {/* Mobile Menu */}
    <div className="single-slider slider-height2 d-flex align-items-center" style={{backgroundImage:`url('assets/img/hero/category.jpg')`}} data-background="assets/img/hero/category.jpg">
      <div className="container">
        <div className="row">
          <div className="col-xl-12">
            <div className="hero-cap text-center">
                  <h2>{header}</h2>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
    )
}
