import React from "react";
import { MDBCarousel,MDBMask,MDBCarouselCaption, MDBCarouselInner, MDBCarouselItem, MDBView, MDBContainer } from
"mdbreact";

const CarouselPage = ({products}) => {
    return (
        <div>
            {
                products.map(products => (
                    <MDBContainer>
      <MDBCarousel
        activeItem={1}
        length={products.photo.length}
        showControls={true}
        showIndicators={true}
        className="z-depth-1"
        // slide
      >
              <MDBCarouselInner>
                  {products && products.photo.map((photo,i) => (
                      
                      <MDBCarouselItem itemId={i+1}>
            <MDBView>
              <img
                className="d-block w-100"
                src={`http://localhost:3000/${photo.path}`}
                alt="First slide"
                          />
                          
          <MDBMask overlay="black-light" />
             </MDBView>
            <MDBCarouselCaption>
                              <h3 className="h3-responsive">{products.title}</h3>
                             
          </MDBCarouselCaption>
          </MDBCarouselItem>
                  ))}
          
    
        </MDBCarouselInner>
                        </MDBCarousel>
                        <center><p>{products.body}</p></center>
            </MDBContainer>
                ))
            }
    
            </div>
  );
}

export default CarouselPage;
