import { combineReducers } from "redux";

function productsApp(state = [{redirect:false,auth}], action) {
        
    switch (action.type) {
        case "FETCH_PRODUCTS":
            return [...state, action.payload]
        case "FETCH_PRODUCT":
            return [...state, action.payload]
        case "FETCH_WOMEN":
            return [...state, action.payload]
        case "FETCH_MEN":
            return [...state, action.payload]
        case "FETCH_ELECTRONICS":
            return [...state, action.payload]
        default:
      return state;
    }
}


function postsApp(state = [], action) {
        
    switch (action.type) {
        case "FETCH_BLOGS":
            return [...state, action.payload]
        case "FETCH_BLOG":
            return [state, ...action.payload]
        
        default:
      return state;
    }
}


function Auth(state = [], action) {
        
    switch (action.type) {
        case "LOGIN":
            return [...state, action.payload]
        
        default:
      return state;
    }
}


export default combineReducers({
    products: productsApp,
    posts: postsApp,
    Auth:Auth
})
