import React, { useEffect,useState } from 'react';
import Header from './Components/header';
import Slider from './Components/slider';
import Footer from './Components/footer';
import CarouselPage from './Components/carosoul';
import {
  MDBCarousel,
  MDBMask,
  MDBCarouselCaption,
  MDBCarouselInner,
  MDBCarouselItem,
  MDBView,
  MDBContainer
} from "mdbreact";
import { connect } from 'react-redux';
import { fetchProduct,createCart } from './redux/actions/index'
import './assets/css/style.css'
import { Redirect } from 'react-router-dom';
function SingleProduct(props) {
  var paramId = props.match.params.id;
  const [cart, setCart] = useState(false);
  useEffect(() => {
    props.fetchProduct(paramId);

    },[])


  const addToCart = () => {
    props.createCart(props.products);
  }

  return (
    <div>
          <Header/>
  {/* slider Area Start*/}
        <Slider heading={"Single Product"}/>
  {/* slider Area End*/}
  {/*================Single Product Area =================*/}
  <div className="product_image_area">
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-lg-12">
                  <MDBContainer style={{ height: "30em" }}>
          <MDBCarousel
            activeItem={1}
            color={"blue"}
            length={2}
            showControls={true}
            showIndicators={true}
            className="z-depth-1"
          // slide
          >
            <MDBCarouselInner>
                  <MDBCarouselItem itemId={i + 1}>
                    <MDBView>
                        <img
                          className="d-block w-100"
                          src={`http://localhost:3000/${props.photo.path[0]}`}
                        alt="First slide"
                      />

                      <MDBMask overlay="black-light" />
                    </MDBView>
                    <MDBCarouselCaption>
                      <h3 className="h3-responsive text-light">
                        {props.products.title}
                      </h3>
                    </MDBCarouselCaption>
                  </MDBCarouselItem>
            </MDBCarouselInner>
          </MDBCarousel>
        </MDBContainer>
              </div>
        <div className="col-lg-8">
          <div className="single_product_text text-center">
                  <h3>{ props.products.title }</h3>
                  <p>
                    { props.products.body }
            </p>
            <div className="card_area"> 
              <div className="add_to_cart">
                <button onClick={addToCart} className="btn_3">add to cart</button>
              </div>
            </div>
          </div>
        </div>        

      </div>
    </div>
  </div>
  {/*================End Single Product Area =================*/}
  {/* subscribe part here */}
  <section className="subscribe_part section_padding">
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-lg-8">
          <div className="subscribe_part_content">
            <h2>Get promotions &amp; updates!</h2>
            <p>Seamlessly empower fully researched growth strategies and interoperable internal or “organic” sources credibly innovate granular internal .</p>
            <div className="subscribe_form">
              <input type="email" placeholder="Enter your mail" />
              <a href="#" className="btn_1">Subscribe</a>
            </div>
          </div>
        </div>
      </div>
    </div>
  </section>
        {/* subscribe part end */}
        <Footer />

</div>
 );
    }

const mapDispatchToProps = (dispatch) => ({
  fetchProduct: (id) => {
    dispatch(fetchProduct(id))
  },
  createCart: (products) => {
    dispatch(createCart(products))
  },

})


const mapStateToProps = (state) => {
  // console.log(state.products);
  return {
    products: state.products.products
  }

}

export default connect(mapStateToProps,mapDispatchToProps)(SingleProduct);
