export default function Auth(state = [], action) {
        
    switch (action.type) {
        case 'SET_AUTHENTICATED':
            return { ...state, authenticated: true }
        case 'SET_UNAUTHENTICATED':
            return initialState;
        case "LOGIN":
            return [...state, action.payload]
        case "SIGNUP":
            return [...state, action.payload]
        
        default:
      return state;
    }
}
