import React from 'react';
import Header from './header';
import Slider from './slider';
import "../assets/css/style.css"
import "../App.css";
import { connect } from 'react-redux';
import {fetchElectronics} from '../actions/index';



class Electronics extends React.Component {
    componentDidMount() {
        this.props.fetchElectronics();
    }
    render() {
        const { electronics } = this.props;
        return (
            <div>
                <Header />
                <Slider heading={'Electronics'} />
                <section className="category-area section-padding30" >
                    <div className="container-fluid">
                        {/* Section Tittle */}
        
                    </div>
                </section>
                <section className="latest-product-area padding-bottom">
                    <div className="container">
                        <div className="row product-btn d-flex justify-content-end align-items-end">
                            {/* Section Tittle */}
                            <div className="col-xl-4 col-lg-5 col-md-5">
                                <div className="section-tittle mb-30">
                                    <h3>Electronics's </h3>
                                </div>
                            </div>
                            <div className="col-xl-8 col-lg-7 col-md-7">
                                <div className="properties__button f-right">
                                    {/*Nav Button  */}
                                    <nav>
                                        <div className="nav nav-tabs" id="nav-tab" role="tablist">
                                            <a className="nav-item nav-link active" id="nav-home-tab" data-toggle="tab" href="#nav-home" role="tab" aria-controls="nav-home" aria-selected="true">All</a>
                                            <a className="nav-item nav-link" id="nav-profile-tab" data-toggle="tab" href="#nav-profile" role="tab" aria-controls="nav-profile" aria-selected="false">New</a>
                                            <a className="nav-item nav-link" id="nav-contact-tab" data-toggle="tab" href="#nav-contact" role="tab" aria-controls="nav-contact" aria-selected="false">Featured</a>
                                            <a className="nav-item nav-link" id="nav-last-tab" data-toggle="tab" href="#nav-last" role="tab" aria-controls="nav-contact" aria-selected="false">Offer</a>
                                        </div>
                                    </nav>
                                    {/*End Nav Button  */}
                                </div>
                            </div>
                        </div>
                        {/* Nav Card */}
                        <div className="tab-content" id="nav-tabContent">
                            {/* card one */}
                            <div className="tab-pane fade show active" id="nav-home" role="tabpanel" aria-labelledby="nav-home-tab">
                                <div className="row">
                                    {electronics && electronics.map(electronics => (
                                        <div className="col-xl-4 col-lg-4 col-md-6">
                                        <div className="single-product mb-60">
                                                <div className="product-img">
                                                    <a href={`single_product/${electronics._id}`}>
                              <img src={`http://localhost:3000/${electronics.photo[0].path}`} alt="logo" />
                              </a>
                                                <div className="new-product">
                                                    <span>New</span>
                                                </div>
                                            </div>
                                            <div className="product-caption">
                                                <div className="product-ratting">
                                                    <i className="far fa-star" />
                                                    <i className="far fa-star" />
                                                    <i className="far fa-star" />
                                                    <i className="far fa-star low-star" />
                                                    <i className="far fa-star low-star" />
                                                    </div>
                                                       <h4><a href={`single_product/${electronics._id}`}>{electronics.title}</a></h4>
                                                <div className="price">
                                                    <ul>
                                                <li>${electronics.price}</li>
                                                <li className="discount">${electronics.discount}</li>
                                                    </ul>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                    ))}
                                    
                                </div>
                            </div>
                            {/* Card two */}
     
                            {/* card foure */}
     
                        </div>
                        {/* End Nav Card */}
                    </div>
                </section>

            </div>
        )
    }
}
const mapStateToProps = state => {  
  return {
    electronics:state.products[0]
  }
}

export default connect(mapStateToProps,{fetchElectronics})(Electronics);
