import React, { Component } from "react";
import '../App.css'
  import { MDBContainer, MDBTabPane, MDBTabContent, MDBNav, MDBNavItem, MDBNavLink } from "mdbreact";
import { fetchElectronics, fetchProducts, fetchFashion } from '../actions/index'
import  {connect} from 'react-redux'
class TabsDefault extends Component {
    
    componentDidMount() {
        this.setState({activeItem:this.props.activeItem})

        // if (this.state.activeItem == "2") {
        //     //   console.log("active:",this.state.activeItem);
              
        //       this.props.fetchFashion();
        //   }
        //   else if (this.state.activeItem == "1") {
        //     //   console.log("active:",this.state.activeItem);
        //       this.props.fetchProducts();

        //   }
        //   else if(this.state.activeItem==="3") {
        //     //   console.log("active:",this.state.activeItem);
        //       this.props.fetchElectronics();

        //   }
        }
    state = {
      activeItem: "0"
    };
    toggle = tab => e => {
      if (this.state.activeItem !== tab) {
        this.setState({
          activeItem: tab
        });
          console.log(this.state.activeItem);
          
          if (this.state.activeItem == "2") {
              console.log("active:",this.state.activeItem);
              
              this.props.fetchFashion();
          }
          else if (this.state.activeItem == "3") {
              console.log("active:",this.state.activeItem);

              this.props.fetchElectronics();
          }
          else {

              this.props.fetchProducts();
          }

      }
    };

    render() {
        const { products } = this.props;
        // console.log(this.props.activeItem,"navtabs");
        
        return (
        <MDBContainer>
        <MDBNav className="nav-tabs mt-5">
          <MDBNavItem>
            <MDBNavLink className="tab mr-4"  link to="#" active={this.state.activeItem === `{${this.props.activeItem}}`} onClick={this.toggle(`{${this.props.activeItem}}`)} role="tab" >
              All
            </MDBNavLink>
          </MDBNavItem>
          <MDBNavItem>
            <MDBNavLink className="tab mr-4" link to="#" active={this.state.activeItem === `{${this.props.activeItem}}`} onClick={this.toggle(`{${this.props.activeItem}}`)} role="tab" >
              Fashion
            </MDBNavLink>
          </MDBNavItem>
          <MDBNavItem>
            <MDBNavLink className="tab" link to="#" active={this.state.activeItem === `{${this.props.activeItem}}`} onClick={this.toggle(`{${this.props.activeItem}}`)} role="tab" >
              Electronics
            </MDBNavLink>
          </MDBNavItem>
        </MDBNav>
        <MDBTabContent activeItem={this.state.activeItem} >
          <MDBTabPane tabId="1" role="tabpanel">
            <div className="row">
                    {products[0] && products[0].map(products => (
                <div className="col-xl-4 col-lg-4 col-md-6">
        <div className="single-product mb-60">
            <div className="product-img">
            <img src={`http://localhost:3000/${products.photo[0].path}`} alt={'product'} />
            <div className="new-product">
                <span>New</span>
            </div>
            </div>
            <div className="product-caption">
            <div className="product-ratting">
                <i className="far fa-star" />
                <i className="far fa-star" />
                <i className="far fa-star" />
                <i className="far fa-star low-star" />
                <i className="far fa-star low-star" />
            </div>
                <h4><a href={`single_product/${products._id}`}>{products.title}</a></h4>
            <div className="price">
                <ul>
                <li>${products.price}</li>
            <li className="discount">${products.discount}</li>
                </ul>
            </div>
            </div>
        </div>
            </div>
            ))}
                </div>
                      
          </MDBTabPane>
          <MDBTabPane tabId="2" role="tabpanel">
            <div className="row">
                    {products[1] && products[1].map(products => (
                <div className="col-xl-4 col-lg-4 col-md-6">
        <div className="single-product mb-60">
            <div className="product-img">
            <img src={`http://localhost:3000/${products.photo[0].path}`} alt={'product'} />
            <div className="new-product">
                <span>New</span>
            </div>
            </div>
            <div className="product-caption">
            <div className="product-ratting">
                <i className="far fa-star" />
                <i className="far fa-star" />
                <i className="far fa-star" />
                <i className="far fa-star low-star" />
                <i className="far fa-star low-star" />
            </div>
                <h4><a href={`single_product/${products._id}`}>{products.title}</a></h4>
            <div className="price">
                <ul>
                <li>${products.price}</li>
            <li className="discount">${products.discount}</li>
                </ul>
            </div>
            </div>
        </div>
            </div>
            ))}
                </div>
          </MDBTabPane>
          <MDBTabPane tabId="3" role="tabpanel">
            <div className="row">
                    {products[2] && products[2].map(products => (
                <div className="col-xl-4 col-lg-4 col-md-6">
        <div className="single-product mb-60">
            <div className="product-img">
