import { combineReducers } from "redux";

function productsApp(state = [], action) {
        
    switch (action.type) {
        case "FETCH_PRODUCTS":
            return [...state, action.payload]
        case "FETCH_PRODUCT":
            console.log("reducers",action.payload);
            
            return [...state, action.payload]
        default:
      return state;
    }
}


export default combineReducers({
    products:productsApp
})
