import React from "react";
import { MDBCarousel,MDBMask,MDBCarouselCaption, MDBCarouselInner, MDBCarouselItem, MDBView, MDBContainer } from
"mdbreact";

const CarouselPage = ({ products }) => {
  
    return (
        <div>
        {products && products.map(products => (
               <MDBContainer>
      <MDBCarousel
              activeItem={1}
              color={'blue'}
        length={products && products.photo.length}
        showControls={true}
        showIndicators={true}
        className="z-depth-1"
        // slide
      >
              <MDBCarouselInner>
                  {products && products.photo.map((photo,i) => (
                      
                      <MDBCarouselItem itemId={i+1}>
            <MDBView >
              <img
                          className="d-block w-100"
                src={`http://localhost:3000/${photo.path}`}
                alt="First slide"
                          />
                          
          <MDBMask overlay="black-light" />
             </MDBView>
            <MDBCarouselCaption>
              <h3 className="h3-responsive text-light">{products.title}</h3>   
          </MDBCarouselCaption>
          </MDBCarouselItem>
                  ))}
          
    
        </MDBCarouselInner>
                        </MDBCarousel>

            </MDBContainer>

          
            ))}
     
            </div>
  );
}

export default CarouselPage;
